.mc4wp-form-fields input[type="email"] {
border: none;
    border: none;
    border-bottom: 1px solid #fff;
    border-radius: 0;
    background: none;
    color: #fff;
    width: 100%;
    padding-left: 0;
}
.mc4wp-form-fields input[type="email"]::placeholder {
  color: #fff;
  opacity: 1; /* Firefox */
}
.mc4wp-form-fields {
    gap: 0;
    position: relative;
}
.mc4wp-form-fields input[type="submit"] {
    width: auto;
    position: absolute;
    right: 0;
    text-transform: none;
    font-size: 16px;
    border-radius: 30px;
    padding: 9px 30px;
    background: #fff;
	color: #000;
}
